import { IProdutos } from './../../interfaces/produtos';
import { Component } from '@angular/core';
import { ProdutosService } from 'src/app/service/produtos.service';

@Component({
  selector: 'app-produto',
  templateUrl: './produto.component.html',
  styleUrls: ['./produto.component.css']
})
export class ProdutoComponent {

      produto: IProdutos[] = [];

      constructor(private produtosService: ProdutosService,){}

      ngOnInit(){
        this.listar();
    }

    listar(){
      this.produtosService.listarProdutos().subscribe( produto => {
        this.produto = produto;
      }, (error) => {
        console.log("Erro ao buscar produtos");
      }
    );
  }
  deletar(id: number) {
    this.produtosService.deletar(id).subscribe(
      () => {
        this.produto = this.produto.filter((produto) => produto.id !== id);
      },
      (error) => {
        console.log("Erro ao excluir produto");
      }
    );
  }
}

<table class="table">
    <thead>
      <tr>
        <th scope="col">Nome</th>
        <th scope="col">Código de Barras</th>
        <th scope="col">Preço</th>
        <th scope="col" >Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let produto of produto">
        <td scope="row">{{produto.nome}}</td>
        <td>{{produto.codigoBarras}}</td>
        <td>{{produto.preco}}</td>
        <td>
            <div class="btn-group" role="group" aria-label="Basic mixed styles example" > 
              <a class="nav-link active" aria-current="page" [routerLink]="'/produtos/editar/' + produto.id">
                <button type="button" class="btn btn-warning">Editar</button>
            </a>
                <button type="button" class="btn btn-danger" (click)="deletar(produto.id)">Excluir</button>
            </div>
        </td>
      </tr>
    </tbody>
  </table>
  <div>
    <a class="nav-link active" aria-current="page" routerLink="/produtos/cadastrar" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">
        <button type="button" class="btn btn-primary btn-lg">Cadastrar produto</button>
    </a>
  </div>
