options: {
  title: {
    display: true, // Mostrar o título
    text: 'Preço dos produtos', // Texto do título
    fontSize: 18, // Tamanho da fonte do título
    color: 'rgba(0, 0, 0, 0.8)', // Cor do título (exemplo: preto com leve transparência)
    font: {
      style: 'bold' // Estilo de fonte em negrito
    }
  },
  scales: {
    y: {
      beginAtZero: true
    }
  },
  plugins: {
    legend: {
      display: false
    }
  }
}
