import { Component, ElementRef, ViewChild, OnInit } from '@angular/core';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}




<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Gráfico de Produtos</title>
    <!-- Inclua a biblioteca Chart.js -->
    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
</head>
<body>
    <h1>Gráfico de Preços de Produtos</h1>
    <!-- Adicione um elemento canvas para o gráfico -->
    <canvas id="myChart" width="400" height="200"></canvas>

    <script>
        // Dados de exemplo (substitua pelos seus dados reais)
        const produtos = [
            { nome: 'Produto A', preco: 10 },
            { nome: 'Produto B', preco: 20 },
            { nome: 'Produto C', preco: 15 },
        ];

        // Acesse o elemento canvas
        const canvas = document.getElementById('myChart');
        const ctx = canvas.getContext('2d');

        // Mapeie os produtos para os rótulos (labels) e valores do gráfico
        const labels = produtos.map(produto => produto.nome);
        const data = produtos.map(produto => produto.preco);

        // Crie o gráfico de barras
        new Chart(ctx, {
            type: 'bar',
            data: {
                labels: labels,
                datasets: [{
                    label: 'Preços dos Produtos',
                    data: data,
                    backgroundColor: 'rgba(75, 192, 192, 0.2)',
                    borderColor: 'rgba(75, 192, 192, 1)',
                    borderWidth: 1
                }]
            },
            options: {
                scales: {
                    y: {
                        beginAtZero: true
                    }
                }
            }
        });
    </script>
</body>
</html>
