import { Component, ElementRef, ViewChild, OnInit } from '@angular/core';
import { IProdutos } from 'src/app/interfaces/produtos';
import { ProdutosService } from 'src/app/service/produtos.service';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  produtos: IProdutos[] = [];
  chart: Chart | undefined;

  @ViewChild('myChart', { static: true }) canvas!: ElementRef;

  constructor(private produtosService: ProdutosService) {}

  ngOnInit() {
    this.produtosService.listarProdutos().subscribe((produtos) => {
      this.produtos = produtos;

      // Criar o gráfico
      const ctx = this.canvas.nativeElement.getContext('2d');

      if (ctx) {
        this.chart = new Chart(ctx, {
          type: 'bar',
          data: {
            labels: produtos.map((produto) => produto.nome),
            datasets: [{
              label: 'Preço',
              data: produtos.map((produto) => produto.preco),
              backgroundColor: ['rgba(65,105,225)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',],
              borderColor: [
                'rgba(65,105,225)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)',
              ],
              borderWidth: 1
            }]
          },
          options: {
            scales: {
              y: {
                beginAtZero: true
              }
            },
            plugins: {
              legend: {
                display: false
              },
              title: {
                display: true,
                text: 'Preço dos produtos',
                color: 'rgba(0,0,0)',
                font: {
                  weight: 'bold',
                  size: 28
                }
              }              
            }
          }
        });
      }
    });
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Document</title>
</head>
<body>
    <div class="card">    
        <canvas #myChart>
        </canvas>
    </div>
</body>
</html>
