import { Component, ElementRef, ViewChild, OnInit } from '@angular/core';
import { Chart } from 'chart.js';
import { IProdutos } from 'src/app/interfaces/produtos';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent{
  @ViewChild('myChart') myChart!: ElementRef;
  produtos: IProdutos[] = []; // Suponha que você tenha uma lista de produtos

  // Método para criar o gráfico
  createChart() {
    if (this.myChart) {
      const canvas: HTMLCanvasElement = this.myChart.nativeElement;
      const ctx = canvas.getContext('2d');

      if (ctx) {
        // Mapeie seus produtos para os rótulos (labels) e valores do gráfico
        const labels = this.produtos.map(produto => produto.nome);
        const data = this.produtos.map(produto => produto.preco);

        const chart = new Chart(ctx, {
          type: 'bar', // Ou outro tipo de gráfico Chart.js
          data: {
            labels: labels,
            datasets: [{
              label: 'Preços dos Produtos',
              data: data,
              backgroundColor: 'rgba(75, 192, 192, 0.2)', // Cor de fundo das barras
              borderColor: 'rgba(75, 192, 192, 1)', // Cor da borda das barras
              borderWidth: 1
            }]
          },
          options: {
            scales: {
              y: {
                beginAtZero: true
              }
            }
          }
        });
      } else {
        console.error('Contexto de desenho nulo.');
      }
    } else {
      console.error('Elemento myChart não encontrado.');
    }
  }

  ngOnInit() {
    this.createChart();
  }
}
