import { Component, OnInit } from '@angular/core';
import { ProdutosService } from 'src/app/service/produtos.service';
import { FormGroup, FormControl, Validators, FormBuilder } from '@angular/forms';
import { IProdutos } from 'src/app/interfaces/produtos';
import { ActivatedRoute } from '@angular/router';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-edicao-produto',
  templateUrl: './edicao-produto.component.html',
  styleUrls: ['./edicao-produto.component.css']
})
export class EdicaoProdutoComponent implements OnInit{

  produto!: IProdutos;
  produtoId!: number;
  produtoForm!: FormGroup;

  constructor(
    private produtoService: ProdutosService,
    private route: ActivatedRoute,
    private formBuilder: FormBuilder
    ){ }

  ngOnInit() {
    this.ConfigurarForm();

      this.route.params.subscribe((params) => {
        const id = +params['id'];
        if (!isNaN(id)) {
          this.produtoId = id; 
          this.produtoService.buscarProdutoId(this.produtoId).subscribe(
            (produto) => {
              this.produto = produto; 
              this.produtoForm.patchValue(produto);
            },
            (error) => {
              console.error('Erro ao buscar o produto:', error);
            }
          );
        }
      });
    }


  atualizarProdutos() {
    const produto: IProdutos = this.produtoForm.value as IProdutos;

    if (this.produto) {
      produto.id = this.produto.id;
      this.produtoService.atualizarProdutos(produto.id, produto).subscribe(
        (result) => {
          Swal.fire({
            icon: 'success',
            title: 'EDIÇÃO CONCLUÍDA!',
            text: 'Produto editado com sucesso',
            showConfirmButton: true,
          });
          this.produtoForm.reset();
          },
          (error) => {
            const { message } = error;
            Swal.fire('Erro ao editar produto', message, 'error');
          });
       }
    }

  ConfigurarForm(){
    this.produtoForm = this.formBuilder.group({
      nome: new FormControl('', [Validators.required,
        Validators.max(100),
        ]),
      codigoBarras: new FormControl('', [Validators.required,
        Validators.pattern('^[0-9]+$'),
        Validators.maxLength(30),
      ]),
      preco: new FormControl(0, [Validators.required,
        Validators.pattern('^[0-9]+$'),
        Validators.min(1),
        Validators.max(100000),
      ]),
    });
  }


  }












<h1>Editar Produto</h1>

<form [formGroup]="produtoForm" (submit)="atualizarProdutos()" novalidate>
    <div class="mb-3">
      <label for="nome" class="form-label" >Nome do produto *</label>
      <input type="text" class="form-control" id="nome" formControlName="nome">
      <div class="invalid-feedback">
        Nome obrigaatorio.
      </div>
    </div>
    <div class="mb-3">
      <label for="text" class="form-label">Código de Barras *</label>
      <input type="text" class="form-control" id="codigoBarras" formControlName="codigoBarras">
    </div>
    <div class="mb-3">
        <label for="number" class="form-label" >Preço *</label>
        <input type="number" class="form-control" id="preco" formControlName="preco">
      </div>
      <button class="btn btn-primary" type="submit" [disabled]="produtoForm.invalid">Salvar</button>
    </form>
