import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { IProdutos } from 'src/app/interfaces/produtos';
import { ProdutosService } from 'src/app/service/produtos.service';

@Component({
  selector: 'app-cadastro-produto',
  templateUrl: './cadastro-produto.component.html',
  styleUrls: ['./cadastro-produto.component.css']
})
export class CadastroProdutoComponent {

  constructor(private produtos: ProdutosService ){}

  produtoForm = new FormGroup({
    nome: new FormControl('', Validators.required),
    codigoBarras: new FormControl('', Validators.required),
    preco: new FormControl(0, Validators.required),
  });


  enviar(){
    const produto: IProdutos = this.produtoForm.value as IProdutos;

    this.produtos.cadastrarProdutos(produto).subscribe();    
  }

}

<h1>Cadastrar Produto</h1>
<form [formGroup]="produtoForm" (submit)="enviar">
    <div class="mb-3">
      <label for="exampleInputEmail1" class="form-label" formControlName="nome">Nome do produto</label>
      <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
    </div>
    <div class="mb-3">
      <label for="exampleInputPassword1" class="form-label" formControlName="codigoBarras">Código de Barras</label>
      <input type="password" class="form-control" id="exampleInputPassword1">
    </div>
    <div class="mb-3">
        <label for="exampleInputPassword1" class="form-label" formControlName="preco">Preço</label>
        <input type="password" class="form-control" id="exampleInputPassword1">
      </div>
    <button type="submit" class="btn btn-primary" type="submit" [disabled]="p.invalid">Cadastrar</button>
  </form>
