import { IProdutos } from './../../interfaces/produtos';
import { Component } from '@angular/core';
import { ProdutosService } from 'src/app/service/produtos.service';

@Component({
  selector: 'app-produto',
  templateUrl: './produto.component.html',
  styleUrls: ['./produto.component.css']
})
export class ProdutoComponent {

      produto: IProdutos[] = [];

      constructor(private produtosService: ProdutosService){}

      ngOnInit(){
        this.listar();
    }

    listar(){
      this.produtosService.listarProdutos().subscribe( produto => {
        this.produto = produto;
      }, (error) => {
        console.log("Erro ao buscar produtos");
      }
    );
  }
}

<table class="table">
    <thead>
      <tr>
        <th scope="col">Nome</th>
        <th scope="col">Código de Barras</th>
        <th scope="col">Preço</th>
        <th scope="col" >Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td scope="row">{{produto.nome}}</td>
        <td>{{produto.codigoBarras}}</td>
        <td>{{produto.preco}}</td>
        <td>
            <div class="btn-group" role="group" aria-label="Basic mixed styles example" > 
                <button type="button" class="btn btn-warning">Editar</button>
                <button type="button" class="btn btn-danger">Excluir</button>
            </div>
        </td>
      </tr>
    </tbody>
  </table>
  <div>
    <a class="nav-link active" aria-current="page" routerLink="/produto/cadastrar" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">
        <button type="button" class="btn btn-primary btn-lg" routerLink="produto/cadastrar" routerLinkActive="active">Cadastrar</button>
    </a>
  </div>
